import React, { Component } from "react";
import { 
    View,
    Text,
    StyleSheet,
    TextInput,
    TouchableOpacity,
    Image
} from "react-native";
import * as firebase from 'firebase'


class RegisterScreen extends Component {
    state = {
        name: "",
        email: "",
        password: "",
        errorMessage: null
    };

    handleSignUp = () =>{
        firebase
        .auth()
        .createUserWithEmailAndPassword(this.state.email, this.state.password)
        .then(userCredentials => {
            return userCredentials.user.updateProfile({
                displayName: this.state.name
            })
        })
        .catch(errror => this.setState({ errorMessage: error.message}));
    };

    render() {
        return (
            <View style={styles.container}>
                <Image source={require("../assets/splash.png")}
                    style={{ height: 100, width: 400, resizeMode: "cover"}}                    
                    >

                    </Image>
                    <Text style={styles.greeting}>
                        {'Buenas, \n empecemos'}
                    </Text>

             <View style={styles.errorMessage}>
                 {this.state.errorMessage && <Text style={styles.error}>{this.state.errorMessage}</Text>}

            </View>
                <View style={styles.form}>
                <View>
                         <Text style={styles.inputTitle}>Nombre Completo </Text>
                         <TextInput 
                         style={styles.input} 
                         autoCapitalize="none"
                         onChangeText={name => this.setState({ name })}
                         value={this.state.name}
                         ></TextInput>
                    </View>

                    <View style={{marginTop: 32 }}>
                         <Text style={styles.inputTitle}>Email</Text>
                         <TextInput 
                         style={styles.input} 
                         autoCapitalize="none"
                         onChangeText={email => this.setState({ email })}
                         value={this.state.email}
                         ></TextInput>
                    </View>
                    <View style={{marginTop: 32 }}>
                         <Text style={styles.inputTitle}>Contraseña</Text>
                         <TextInput 
                         style={styles.input} 
                         secureTextEntry 
                         autoCapitalize="none"
                         onChangeText={password => this.setState({ password })}
                         value={this.state.password}
                         ></TextInput>
                    </View> 
                </View>


                <TouchableOpacity style={styles.button} onPress={this.handleSignUp}>
                    <Text style={{color:"#FFF", fontWeight: "600"}}>Registrar-se</Text>
                </TouchableOpacity>

                <TouchableOpacity style={{alignSelf: "center", marginTop: 32}} onPress={()=> this.props.navigation.navigate("Login")}>
                    <Text style={{ color: "#414959", fontSize: 13}}>
                        Ya estas registrado? <Text style={{ fontWeight: "600", color: "#173F5F" }}>Iniciar Sesión</Text>
                    </Text>
                </TouchableOpacity>
            </View>

        );
    }
}
export default RegisterScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent:"center",
        backgroundColor: "#91DAFF"
    },
    greeting:{
        marginTop: 32,
        fontSize:25,
        fontWeight:"700",
        textAlign: "center",
        color: "#173F5F"
    },
    errorMessage:{
        height:72,
        alignItems: "center",
        justifyContent: "center",
        marginHorizontal: 30
    },
    error:{
        color: "#E9446A",
        fontSize: 13,
        fontWeight: "600",

    },
    form:{
         marginBottom: 48,
         marginHorizontal: 30
    },
    inputTitle:{
        color:"#173F5F",
        fontSize:10,
        textTransform: "uppercase"
    },
    input: {
        borderBottomColor: "#173F5F",
        borderBottomWidth:  StyleSheet.hairlineWidth,
        height: 40,
        fontSize: 15,
        color: "#161F3D"
    },
    button:{
        marginHorizontal: 30,
        backgroundColor: "#173F5F",
        borderRadius: 15,
        height: 52,
        alignItems: "center",
        justifyContent: "center"

    }
});
